const workflowStaticData = $getWorkflowStaticData('global');

if (!workflowStaticData.processedItems) {
  workflowStaticData.processedItems = [];
}

const processedItemsSet = new Set(workflowStaticData.processedItems);

const items = $input.all();
const newItems = [];

for (const item of items) {
  const id = item.json.guid || item.json.link;

  if (!processedItemsSet.has(id)) {
    let texto = item.json.contentSnippet || item.json.content || item.json.summary || item.json.title;
    const oraciones = texto.split('.').map(s => s.trim()).filter(s => s.length > 0);
    const numOraciones = Math.min(Math.max(2, oraciones.length), 4);
    const resumen = oraciones.slice(0, numOraciones).join('. ') + '.';

    newItems.push({
      json: {
        ...item.json,
        resumen: resumen,
        date: item.json.pubDate || item.json.isoDate || new Date().toISOString(),
        id: id
      }
    });

    processedItemsSet.add(id);
  }
}

workflowStaticData.processedItems = Array.from(processedItemsSet);
workflowStaticData.lastExecution = new Date().toISOString();

newItems.sort((a, b) => new Date(b.json.date) - new Date(a.json.date));

const noticiasPorFuente = {};

for (const item of newItems) {
  const url = item.json.link;
  let fuente = 'Fuente';

  const dominios = {
    'diarioacuicola.cl': 'Diario Acu√≠cola',    
    'salmonesaustral.cl': 'Salmones Austral',
    'infosalmon.cl': 'InfoSalmon',
    'salmonchile.cl': 'SalmonChile',
