    'aqua.cl': 'Aqua',
    'mundoacuicola.cl': 'Mundo Acuicola',
    'cookeaqua.cl': 'Cooke Aqua',
    'partnerfish.cl': 'Partner Fish',
    'ventisqueros.com': 'Ventisqueros',
    'fiordoaustral.com': 'Fiordo Austral',
    'salmonexpert.cl': 'Salmon Expert'
  };

  for (const dominio in dominios) {
    if (url.includes(dominio)) {
      fuente = dominios[dominio];
      break;
    }
  }

  if (!noticiasPorFuente[fuente]) {
    noticiasPorFuente[fuente] = [];
  }

  noticiasPorFuente[fuente].push(item.json);
}

let htmlNoticias = '';

Object.entries(noticiasPorFuente).forEach(([fuente, noticiasGrupo], indexGrupo) => {
  htmlNoticias += `
  <div class="source-section">
    <h3 class="source-title">${fuente}</h3>
  </div>
  `;

  noticiasGrupo.forEach((noticia, indexNoticia) => {
    const fechaNoticia = noticia.date ? new Date(noticia.date).toLocaleDateString('es-ES', { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' }) : '';

    htmlNoticias += `
    <article class="news-item">
      <h2>${noticia.title}</h2>
      <div class="meta-row">
        <div class="source-tag">${fuente}</div>
        <div class="date-tag">${fechaNoticia}</div>
      </div>
      <div class="summary">${noticia.resumen}</div>
      <div class="button-container">
        <a href="${noticia.link}" class="read-more" target="_blank">Leer m√°s</a>
      </div>
    </article>
    `;

    if (indexNoticia < noticiasGrupo.length - 1) {
      htmlNoticias += `<div class="news-separator"></div>`;
    }
  });

  if (indexGrupo < Object.keys(noticiasPorFuente).length - 1) {
    htmlNoticias += `<div class="source-group-separator"></div>`;
  }
});

